import axios from "axios"
import { Navigate } from 'react-router-dom';

const API_URL = 'https://638db528aefc455fb2a96a69.mockapi.io/data';
const API_URL_SIGNUP = '/auth/signup';
const API_URL_SIGNIN = '/auth/signin';
const register = (username, email, password,age) => {
  return axios.post(API_URL + API_URL_SIGNUP, {
    username,
    email,
    password,
    age,
  }).then(()=> {

    alert('Пользователь успешно зарегистрирован')



  });
};

const login = (username, password) => {
  return axios
    .post(API_URL +  API_URL_SIGNIN, {
      username,
      password,
    })
    .then((response) => {
      if (response.data.accessToken) {
        alert('you have been registered')
        localStorage.setItem("user", JSON.stringify(response.data));
        return <Navigate to="/profile" />;

      }

      return response.data;
    });
};

const logout = () => {
  localStorage.removeItem("user");
};

export default {
  register,
  login,
  logout,
};
